<template>    <div class="delivery_wrap">        <el-tabs v-model="choose" type="card">            <!--全部-->            <el-tab-pane label="选择物流信息" name="does">                <div class="delivery_info">                    <div class="has_delivery">                        <div class="has_delivery_title">                            <p>公司名称</p>                            <p>运单号</p>                            <p>操作</p>                        </div>                        <div class="has_delivery_inner" v-for="(item,index) in delivery" :key="item.label_id">                            <p>{{item.name}}</p>                            <el-input v-model="input[index]" placeholder="请输入快递运单号" @change="change"></el-input>                            <p>                                <span @click="handleDelivery(true,item.label_id,index)">确定</span>                            </p>                        </div>                    </div>                </div>            </el-tab-pane>            <!--已付款-->            <el-tab-pane label="无需物流" name="doesNot">                <div class="delivery_info">                    <div class="no_delivery">                        <span>如果该商品无需物流运送（如虚拟产品），你可以点击</span>                        <span @click="handleDelivery(false)">确定</span>                    </div>                </div>            </el-tab-pane>        </el-tabs>    </div></template><script>    import http from '../../../libs/http'    export default {        name: 'delivery',        props:['id'],        data(){            return{                choose:"does",                delivery:[],                input:[]            }        },        mounted(){            this.apiGet('labels?id=1083').then((res)=>{                if(res){                    this.delivery = res                }            })        },        methods:{            change(){                console.log(this.input)            },            handleDelivery(Is,id,index){                let data;                if(Is){                    data = {                        status:6                    }                }else{                    data = {                        status:6,                        logistics_label_id:id,                        logistics_no:this.input[index]                    }                }                this.apiPost('api/order/update/' + this.id,data).then((res)=>{                    console.log(res)                    if(res){                        this.$emit('on-handle-list',false)                    }                })            }        },        mixins:[http]    }</script><style lang="stylus">    .delivery_wrap        background-color white        .delivery_info            border 1px solid #EBEBEB            padding 24px 28px            .no_delivery                width:592px;                height:40px;                color #999999                font-size 14px                line-height 40px                margin-bottom 20px                background:rgba(245,245,245,1);                border:1px solid rgba(204,204,204,1);                span:nth-child(1)                    margin-right 28px                span:nth-child(2)                    height:21px;                    color #24BFFF                    font-size 13px                    cursor pointer                    line-height 21px                    padding 3px 14px                    border-radius:30px;                    background:rgba(247,253,255,1);                    border:1px solid rgba(36, 191, 255, 1);                span:nth-child(2):hover                    color white                    background-color #24BFFF            .has_delivery                width:592px;                border:1px solid rgba(204,204,204,1);                .has_delivery_title                    display flex;                    height 33px;                    line-height 33px                    justify-content space-around                    p                        color #333333                        font-size 12px                        align-self center                .has_delivery_inner                    height 33px                    display flex                    line-height 33px                    justify-content space-around                    border-top:1px solid rgba(204,204,204,1);                    p                        width 150px                        color #666666                        font-size 12px                        align-self center                        text-align center                        span                            width:55px;                            color white                            cursor pointer                            padding 3px 14px                            border-radius:30px;                            background:rgba(36,191,255,1);                            border:1px solid rgba(36, 191, 255, 1);                    p:nth-child(1)                        width 130px                    .el-input                        width 180px                        height 21px                        margin-left 30px                        .el-input__inner                            width 180px                            height 21px                            font-size 12px                            border-radius 0                            line-height 21px    .el-tabs__item{        border: none;        height: 50px;        line-height: 50px;        color: #999999;    }    .el-tabs__item:nth-child(1){        border-radius: 3px 0 0 0;    }    .el-tabs__header{        margin: 0;        background:rgba(245,246,247,1);        border-radius: 7px 7px 0 0;        box-sizing: border-box;    }    .el-tabs--card>.el-tabs__header{        border: 1px solid rgba(235, 235, 235, 1);        border-bottom: none;        height: 50px;    }    .el-tabs--card>.el-tabs__header .el-tabs__nav{        border: none;    }    .el-tabs--card>.el-tabs__header .el-tabs__item.is-active{        border-top: 3.5px solid rgba(36,191,255,1);        line-height: 42.5px;        color: #4d4d4d;        background: #fff;    }</style>